xml.query:
drop table if exists tmp.a_metrics_daily_stats; drop table if exists tmp.dau_daily_dash;
create table tmp.a_metrics_daily_stats engine=myisam as select stat_date, game_id, client_id, cast('dau' as char(8)) as metric, sum(value) as value from star.s_game_day where metric='ActiveUsersByDay' and stat_date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3,4 order by 1,2,3,4 ;
#Installs insert into tmp.a_metrics_daily_stats select stat_date, game_id, client_id, 'installs' as metrics, sum(value) as value from star.s_game_day where metric='NewUsers' and stat_date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3,4 order by 1,2,3,4 ;
#MAU create temporary table tmp.dau_daily_dash(index(stat_date, user_id)) engine=myisam as select game_id, client_id, stat_date, user_id from star.s_user_day where stat_date between date_sub('$date$', interval $days$+37 day) and '$date$' group by 1,2,3,4 ;
# 7 Day Averages insert into tmp.a_metrics_daily_stats select stat_date, game_id, client_id, '7dau' as metric, round(sum(value)/7.0) as value from ( select d.date as stat_date, a.game_id, a.client_id, count(distinct a.user_id) as value from tmp.dau_daily_dash a, lookups.d_date d where a.stat_date between date_sub(d.date, interval 6 day) and d.date and d.date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3 ) a group by 1,2,3,4 order by 1,2,3,4 ;
insert into tmp.a_metrics_daily_stats select stat_date, game_id, client_id, 'mau' as metric, sum(value) as value from ( select d.date as stat_date, a.game_id, a.client_id, count(distinct a.user_id) as value from tmp.dau_daily_dash a, lookups.d_date d where a.stat_date between date_sub(d.date, interval 29 day) and d.date and d.date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3 ) a group by 1,2,3,4 order by 1,2,3,4 ;
create temporary table tmp.fx_rate as select b.currency_code, b.fx_rate from ( select currency_code, max(fx_date) as fx_date from lookups.l_fx_rate group by 1 ) a, lookups.l_fx_rate b where a.currency_code=b.currency_code and a.fx_date=b.fx_date group by 1,2 ;
create temporary table tmp.revenue(index(stat_date, currency_code)) engine=myisam as select start_date as stat_date, currency_of_proceeds as currency_code, sum(units*net_revenue)/0.7 as gross_revenue from star.itunes_sales where start_date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2 ;
insert into tmp.revenue select stat_date, 'USD' as currency_code, sum(revenue/0.7) as gross_revenue from star.app_figures where stat_date between date_sub('$date$', interval $days$+7 day) and '$date$' and game_id in (21,24) group by 1,2 ;
insert into tmp.a_metrics_daily_stats SELECT stat_date, -2, -2, 'revenue' as metric, sum(gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end) as value from tmp.revenue a left join lookups.l_fx_rate b on a.stat_date=b.fx_date and a.currency_code=b.currency_code join tmp.fx_rate c on a.currency_code=c.currency_code where stat_date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3,4 order by 1,2,3,4 ;
insert into tmp.a_metrics_daily_stats SELECT stat_date as stat_date, -2, -2, 'units' as metric, sum(case when net_revenue>0 then units else 0 end) as value from ( select start_date as stat_date, net_revenue, units from star.itunes_sales where start_date between date_sub('$date$', interval $days$+7 day) and '$date$' union select stat_date, revenue, downloads as units from star.app_figures where stat_date between date_sub('$date$', interval $days$+7 day) and '$date$' ) a group by 1,2,3,4 order by 1,2,3,4 ;
select stat_date as Date, max(a.dau) as DAU, max(a.installs) as Installs, max(a.mau) as MAU, max(a.engagement) as Engagement, max(a.revenue) as Revenue, max(a.units) as Units, sum(b.dau)/($days$+1) as Avg_DAU, sum(b.installs)/($days$+1) as Avg_Installs, sum(b.mau)/($days$+1) as Avg_MAU, max(b.engagement) as Avg_Engagement, sum(b.revenue)/($days$+1) as Avg_Revenue, sum(b.units)/($days$+1) as Avg_Units from ( select stat_date, sum(case when metric='dau' then value else 0 end) as dau, sum(case when metric='installs' then value else 0 end) as installs, sum(case when metric='mau' then value else 0 end) as mau, 100*sum(case when metric='7dau' then value else 0 end)/ sum(case when metric='mau' then value else 0 end) as engagement, sum(case when metric='revenue' then value else 0 end) as revenue, sum(case when metric='units' then value else 0 end) as units from tmp.a_metrics_daily_stats where stat_date between date_sub('$date$', interval $days$+1 day) and '$date$' group by 1 ) a, ( select sum(case when metric='dau' then value else 0 end) as dau, sum(case when metric='installs' then value else 0 end) as installs, sum(case when metric='mau' then value else 0 end) as mau, 100*sum(case when metric='7dau' then value else 0 end)/ sum(case when metric='mau' then value else 0 end) as engagement, sum(case when metric='revenue' then value else 0 end) as revenue, sum(case when metric='units' then value else 0 end) as units from tmp.a_metrics_daily_stats where stat_date between date_sub('$date$', interval $days$+1 day) and '$date$' ) b group by 1 order by 1 ;

drop table if exists tmp.revenue;
create temporary table tmp.revenue(index(stat_date, currency_code)) engine=myisam as select case when sku='28278' then 15 when sku='28413' then 22 when sku='28475' then 11 when title like 'Asteroids%Gunner%' then 18 when upper(title) like 'ASTFLU%' then 7 when upper(title) like 'ASTFNA%' then 18 when upper(title) like 'ATGIOS%' then 1 when upper(title) like 'BREBOO%' then 5 when upper(title) like 'BREBOP%' then 15 when upper(title) like 'CENTOR%' then 22 when upper(title) like 'CIRCU%' then 16 when upper(title) like 'DDWAR%' then 11 when upper(title) like 'NEWOUT%' then 23 when upper(title) like 'PNGWRL%' then 25 else -2 end as game_id, 1 as client_id, start_date as stat_date, currency_of_proceeds as currency_code, sum(units*net_revenue)/0.7 as gross_revenue from star.itunes_sales where start_date between date_sub('$date$', interval $days$+7 day) and '$date$' and net_revenue!=0 group by 1,2,3,4 ;
drop table if exists tmp.fx_rate;
create temporary table tmp.fx_rate as select b.currency_code, b.fx_rate from ( select currency_code, max(fx_date) as fx_date from lookups.l_fx_rate group by 1 ) a, lookups.l_fx_rate b where a.currency_code=b.currency_code and a.fx_date=b.fx_date group by 1,2 ;
insert into tmp.revenue select 21 as game_id, 1 as client_id, stat_date, 'USD' as currency_code, sum(revenue/0.7) as gross_revenue from star.app_figures where stat_date between date_sub('$date$', interval $days$+7 day) and '$date$' and game_id in (21,24) group by 1,2,3 ;
SELECT case when a.game_id in (21,24) then 'Super Bunny Breakout' when a.game_id=-2 then 'Other' else game_name end as Game, sum(case when a.stat_date='$date$' then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end) as Revenue, 100.0*sum(case when a.stat_date='$date$' then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end)/ sum(case when a.stat_date=date_sub('$date$', interval 1 day) then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end)-100 as 'Rev DoD % Change', 100.0*sum(case when a.stat_date='$date$' then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end)/ sum(case when a.stat_date=date_sub('$date$', interval 7 day) then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end)-100 as 'Rev WoW % Change', sum(case when a.stat_date='$date$' then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end)/ max(case when a.stat_date='$date$' then d.value else 0 end) as 'Revenue/DAU', 100.0*(sum(case when a.stat_date='$date$' then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end)/ max(case when a.stat_date='$date$' then d.value else 0 end))/ (sum(case when a.stat_date=date_sub('$date$', interval 1 day) then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end)/ max(case when a.stat_date=date_sub('$date$',interval 1 day) then d.value else 0 end))-100 as 'Rev/DAU DoD % Change', 100.0*(sum(case when a.stat_date='$date$' then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end)/ max(case when a.stat_date='$date$' then d.value else 0 end))/ (sum(case when a.stat_date=date_sub('$date$', interval 7 day) then gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end else 0 end)/ max(case when a.stat_date=date_sub('$date$',interval 7 day) then d.value else 0 end))-100 as 'Rev/DAU WoW % Change' from tmp.revenue a left join lookups.l_fx_rate b on a.stat_date=b.fx_date and a.currency_code=b.currency_code join tmp.fx_rate c on a.currency_code=c.currency_code left join lookups.l_flurry_game g on a.game_id=g.game_id and a.client_id=g.device_id join tmp.a_metrics_daily_stats d on a.client_id=d.client_id and case when a.game_id in (15,22,11,18,7,1,5,15,16,23,25,21,24) then a.game_id else -2 end = case when d.game_id in (15,22,11,18,7,1,5,15,16,23,25,21,24) then d.game_id else -2 end and a.stat_date=d.stat_date and d.metric='dau' where a.stat_date in ('$date$',date_sub('$date$',interval 1 day),date_sub('$date$',interval 7 day)) group by 1 order by 2 desc ;
