xml.query:
drop table if exists tmp.a_metrics_daily_stats; drop table if exists tmp.dau_daily_dash;
create table tmp.a_metrics_daily_stats engine=myisam as select stat_date, game_id, client_id, cast('dau' as char(8)) as metric, sum(value) as value from star.s_game_day where metric='ActiveUsersByDay' and stat_date between date_sub(date_sub(curdate(), interval 1 day), interval $days+7 day) and date_sub(curdate(), interval 1 day) group by 1,2,3,4 order by 1,2,3,4 ;
#Installs insert into tmp.a_metrics_daily_stats select stat_date, game_id, client_id, 'installs' as metrics, sum(value) as value from star.s_game_day where metric='NewUsers' and stat_date between date_sub(date_sub(curdate(), interval 1 day), interval $days+7 day) and date_sub(curdate(), interval 1 day) group by 1,2,3,4 order by 1,2,3,4 ;
#MAU create temporary table tmp.dau_daily_dash(index(stat_date, user_id)) engine=myisam as select game_id, client_id, stat_date, user_id from star.s_user_day where stat_date between date_sub(date_sub(curdate(), interval 1 day), interval $days+37 day) and date_sub(curdate(), interval 1 day) group by 1,2,3,4 ;
# 7 Day Averages insert into tmp.a_metrics_daily_stats select stat_date, game_id, client_id, '7dau' as metric, round(sum(value)/7.0) as value from ( select d.date as stat_date, a.game_id, a.client_id, count(distinct a.user_id) as value from tmp.dau_daily_dash a, lookups.d_date d where a.stat_date between date_sub(d.date, interval 6 day) and d.date and d.date between date_sub(date_sub(curdate(), interval 1 day), interval $days day) and date_sub(curdate(), interval 1 day) group by 1,2,3 ) a group by 1,2,3,4 order by 1,2,3,4 ;
insert into tmp.a_metrics_daily_stats select stat_date, game_id, client_id, 'mau' as metric, sum(value) as value from ( select d.date as stat_date, a.game_id, a.client_id, count(distinct a.user_id) as value from tmp.dau_daily_dash a, lookups.d_date d where a.stat_date between date_sub(d.date, interval 29 day) and d.date and d.date between date_sub(date_sub(curdate(), interval 1 day), interval $days+7 day) and date_sub(curdate(), interval 1 day) group by 1,2,3 ) a group by 1,2,3,4 order by 1,2,3,4 ;
create temporary table tmp.fx_rate as select b.currency_code, b.fx_rate from ( select currency_code, max(fx_date) as fx_date from lookups.l_fx_rate group by 1 ) a, lookups.l_fx_rate b where a.currency_code=b.currency_code and a.fx_date=b.fx_date group by 1,2 ;
create temporary table tmp.revenue(index(stat_date, currency_code)) engine=myisam as select start_date as stat_date, currency_of_proceeds as currency_code, sum(units*net_revenue)/0.7 as gross_revenue from star.itunes_sales where start_date between date_sub(date_sub(curdate(), interval 1 day), interval $days+7 day) and date_sub(curdate(), interval 1 day) group by 1,2 ;
insert into tmp.revenue select stat_date, 'USD' as currency_code, sum(revenue/0.7) as gross_revenue from star.app_figures where stat_date between date_sub(date_sub(curdate(), interval 1 day), interval $days+7 day) and date_sub(curdate(), interval 1 day) and game_id in (21,24) group by 1,2 ;
insert into tmp.a_metrics_daily_stats SELECT stat_date, -2, -2, 'revenue' as metric, sum(gross_revenue/case when b.fx_rate is null then c.fx_rate else b.fx_rate end) as value from tmp.revenue a left join lookups.l_fx_rate b on a.stat_date=b.fx_date and a.currency_code=b.currency_code join tmp.fx_rate c on a.currency_code=c.currency_code where stat_date between date_sub(date_sub(curdate(), interval 1 day), interval 30+7 day) and date_sub(curdate(), interval 1 day) group by 1,2,3,4 order by 1,2,3,4 ;
insert into tmp.a_metrics_daily_stats SELECT stat_date as stat_date, -2, -2, 'units' as metric, sum(case when net_revenue>0 then units else 0 end) as value from ( select start_date as stat_date, net_revenue, units from star.itunes_sales where start_date between date_sub(date_sub(curdate(), interval 1 day), interval $days+7 day) and date_sub(curdate(), interval 1 day) union select stat_date, revenue, downloads as units from star.app_figures where stat_date between date_sub(date_sub(curdate(), interval 1 day), interval $days+7 day) and date_sub(curdate(), interval 1 day) ) a group by 1,2,3,4 order by 1,2,3,4 ;
select stat_date as Date, max(a.dau) as DAU, max(a.installs) as Installs, max(a.mau) as MAU, max(a.engagement) as Engagement, max(a.revenue) as Revenue, max(a.units) as Units, sum(b.dau)/($days+1) as Avg_DAU, sum(b.installs)/($days+1) as Avg_Installs, sum(b.mau)/($days+1) as Avg_MAU, max(b.engagement) as Avg_Engagement, sum(b.revenue)/($days+1) as Avg_Revenue, sum(b.units)/($days+1) as Avg_Units from ( select stat_date, sum(case when metric='dau' then value else 0 end) as dau, sum(case when metric='installs' then value else 0 end) as installs, sum(case when metric='mau' then value else 0 end) as mau, 100*sum(case when metric='7dau' then value else 0 end)/ sum(case when metric='mau' then value else 0 end) as engagement, sum(case when metric='revenue' then value else 0 end) as revenue, sum(case when metric='units' then value else 0 end) as units from tmp.a_metrics_daily_stats where stat_date between date_sub(date_sub(curdate(), interval 1 day), interval $days day) and date_sub(curdate(), interval 1 day) group by 1 ) a, ( select sum(case when metric='dau' then value else 0 end) as dau, sum(case when metric='installs' then value else 0 end) as installs, sum(case when metric='mau' then value else 0 end) as mau, 100*sum(case when metric='7dau' then value else 0 end)/ sum(case when metric='mau' then value else 0 end) as engagement, sum(case when metric='revenue' then value else 0 end) as revenue, sum(case when metric='units' then value else 0 end) as units from tmp.a_metrics_daily_stats where stat_date between date_sub(date_sub(curdate(), interval 1 day), interval $days day) and date_sub(curdate(), interval 1 day) ) b group by 1 order by 1 ;
