xml.query:
drop table if exists tmp.a_metrics; create table tmp.a_metrics engine=myisam as select stat_date, game_id, client_id, cast('dau' as char(8)) as metric, sum(value) as value from star.s_game_day where metric='ActiveUsersByDay' and stat_date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3,4 order by 1,2,3,4 ;
#Installs insert into tmp.a_metrics select stat_date, game_id, client_id, 'installs' as metrics, sum(value) as value from star.s_game_day where metric='NewUsers' and stat_date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3,4 order by 1,2,3,4 ;
#MAU create temporary table tmp.dau(index(stat_date, user_id)) engine=myisam as select game_id, client_id, stat_date, user_id from star.s_user_day where stat_date between date_sub('$date$', interval $days$+37 day) and '$date$' group by 1,2,3,4 ;
# 7 Day Averages insert into tmp.a_metrics select stat_date, game_id, client_id, '7dau' as metric, round(sum(value)/7.0) as value from ( select d.date as stat_date, a.game_id, a.client_id, count(distinct a.user_id) as value from tmp.dau a, lookups.d_date d where a.stat_date between date_sub(d.date, interval 6 day) and d.date and d.date between date_sub('$date$', interval $days$ day) and '$date$' group by 1,2,3 ) a group by 1,2,3,4 order by 1,2,3,4 ;
insert into tmp.a_metrics select stat_date, game_id, client_id, 'mau' as metric, sum(value) as value from ( select d.date as stat_date, a.game_id, a.client_id, count(distinct a.user_id) as value from tmp.dau a, lookups.d_date d where a.stat_date between date_sub(d.date, interval 29 day) and d.date and d.date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3 ) a group by 1,2,3,4 order by 1,2,3,4 ;
create temporary table tmp.revenue(index(stat_date, currency_code)) engine=myisam as select start_date as stat_date, currency_of_proceeds as currency_code, sum(units*net_revenue)/0.7 as gross_revenue from star.itunes_sales where start_date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2 ;
insert into tmp.a_metrics SELECT stat_date, -2, -2, 'revenue' as metric, sum(gross_revenue/case when fx_rate is null then 1 else fx_rate end) as value from tmp.revenue a left join lookups.l_fx_rate b on a.stat_date=b.fx_date and a.currency_code=b.currency_code where stat_date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3,4 order by 1,2,3,4 ;
insert into tmp.a_metrics SELECT start_date as stat_date, -2, -2, 'units' as metric, sum(case when net_revenue>0 then units else 0 end) as value from star.itunes_sales where start_date between date_sub('$date$', interval $days$+7 day) and '$date$' group by 1,2,3,4 order by 1,2,3,4 ;
select stat_date as Date, sum(case when metric='dau' then value else 0 end) as DAU, sum(case when metric='dau' then value else 0 end)/$days$ as Avg_DAU, sum(case when metric='installs' then value else 0 end) as Installs, sum(case when metric='installs' then value else 0 end) as Avg_Installs, sum(case when metric='mau' then value else 0 end) as MAU, 100*sum(case when metric='7dau' then value else 0 end)/ sum(case when metric='mau' then value else 0 end) as Engagement, (100*sum(case when metric='7dau' then value else 0 end)/ sum(case when metric='mau' then value else 0 end))/$days$ as Avg_Engagement, sum(case when metric='mau' then value else 0 end) as MAU, sum(case when metric='mau' then value else 0 end)/$days$ as Avg_MAU, sum(case when metric='revenue' then value else 0 end) as Revenue, sum(case when metric='revenue' then value else 0 end)/$days$ as Avg_Revenue, sum(case when metric='units' then value else 0 end) as Units, sum(case when metric='units' then value else 0 end)/$days$ as Avg_Units from tmp.a_metrics where stat_date between date_sub('$date$', interval $days$ day) and '$date$' group by 1 order by 1 ;
