xml.query:
drop table if exists tmp.dau;
create temporary table tmp.dau(index(user_id), index(stat_date)) engine=MyIsam as select stat_date, user_id from star.s_user_day where game_id=$gameid and client_id=$clientid group by 1,2 ;
drop table if exists tmp.dau_2;
create temporary table tmp.dau_2(index(user_id), index(stat_date)) engine=MyIsam as select * from tmp.dau;
drop table if exists tmp.last_date;
create table tmp.last_date(index(last_date)) engine=MyIsam as select user_id, max(stat_date) as last_date from tmp.dau group by 1 order by 1,2 ;
drop table if exists tmp.quitters;
create table tmp.quitters as select a.user_id, d.date as quit_date from tmp.last_date a, lookups.d_date d where datediff(d.date, last_date)=31 and d.date between '$startdate' and '$enddate' group by 1,2 ;
-- Agg Installs select quit_date as Date, count(distinct user_id) as Quitters from tmp.quitters where quit_date between '$startdate' and '$enddate' group by 1,2 order by 1 ;
